mapper.select:
SELECT PRO_USE_ID,USE_NAME
FROM ADVICE_VIEW
WHERE STUD_USE_ID = #{stud_use_id}

mapper.update:
UPDATE ADVICE
SET AD_STAT = '대기'
WHERE AD_NO = #{ad_no}

UPDATE ADVICE
SET AD_STAT = '연기',
AD_TIME = #{ad_time},
AD_RETURN = #{ad_return}
WHERE AD_NO = #{ad_no}

UPDATE ADVICE
SET AD_STAT = '진행중',
AD_CHANNEL = #{channelId}		
WHERE AD_NO = #{ad_no}
