mapper.select:
select *
from score_view
where use_id = #{id}
and lec_makeyear = #{year}
and lec_makesemester = #{semes}
and CB_ACQUISTIONYN = 'Y'

mapper.delete:
DELETE FROM COURSE_BREAKDOWN
WHERE CB_LEC_NO = #{lec_no}
AND CB_STUD_USE_ID = #{id}

mapper.update:
UPDATE LECTURE
SET LEC_PERSONS_COUNT = LEC_PERSONS_COUNT-1
WHERE LEC_NO = #{lec_no}
