mapper.select:
SELECT
USE_NAME, LN_NO, LN_PRO_USE_ID, AF_NO,LN_LEC_NO, LN_TITLE, LN_CONTENT, AF_AFTERNAME, AF_PATH, TO_CHAR(SYSDATE,'YYYY-MM-DD')LN_DATE
FROM CYBER_LECTURENOTICEVIEWVO WHERE LN_LEC_NO = #{stu_lec_no}

SELECT
USE_NAME, LN_NO, LN_PRO_USE_ID, AF_NO, LN_LEC_NO, LN_TITLE, LN_CONTENT, AF_AFTERNAME, AF_PATH, TO_CHAR(SYSDATE,'YYYY-MM-DD')LN_DATE
FROM CYBER_LECTURENOTICEVIEWVO WHERE LN_NO = #{ln_no}

mapper.update:

UPDATE ATTACHMENT_FILE

SET
AF_REALNAME = #{af_realname},
AF_AFTERNAME = #{af_aftername},
AF_PATH = #{af_path}

WHERE AF_NO = #{af_no}

	
	UPDATE LECTURE_NOTICE
	SET

	LN_TITLE = #{ln_title},
	LN_CONTENT = #{ln_content}
	
	WHERE LN_NO = #{ln_no}
