mapper.insert:
	INSERT INTO QUESTION_BOARD(
	QB_NO,
	QB_STUD_USE_ID,
	QB_LEC_NO,
	QB_AF_NO,
	QB_TITLE,
	QB_CONTENT,
	QB_DATE)

	VALUES(QUESTION_BOARD_SEQ.NEXTVAL
	,#{qb_stud_use_id}
	,#{qb_lec_no}
	,#{qb_af_no}
	,#{qb_title}
	,#{qb_content}
	,SYSDATE)

mapper.update:
UPDATE QUESTION_BOARD

SET                      
QB_TITLE = #{qb_title},
QB_CONTENT = #{qb_content}

WHERE QB_NO = #{qb_no}

UPDATE	ATTACHMENT_FILE 
SET		AF_REALNAME = #{af_realname},
		AF_AFTERNAME = #{af_aftername},
		AF_PATH = #{af_path}
WHERE	AF_NO = #{af_no}

UPDATE QUES_COMMENTS
SET
qc_content = #{comment}
 
WHERE 
qc_qb_no = #{qb_no}
