mapper.update:
UPDATE 	PROFESSOR
SET 	PRO_POSITION = #{pro_position}
	 	,PRO_ENGNAME = #{pro_engname}
		,PRO_PHONE = #{pro_phone}
		,PRO_ZIPCODE = #{pro_zipcode}
		,PRO_ADD = #{pro_add}
		,PRO_ADDDETAIL = #{pro_adddetail}
		,PRO_HOB = #{pro_hob}
		,PRO_EMAIL = #{pro_email}
WHERE	PRO_USE_ID = #{pro_use_id}

UPDATE 	USERS
SET 	USE_NAME = #{use_name}
		,ENABLED = #{enabled}
WHERE	USE_ID = #{use_id}

UPDATE USERS
SET ENABLED = '0'
WHERE USE_ID = #{pro_use_id}
